import Swal from "sweetalert2";
export function SwalFire(title, text, icon) {
  Swal.fire({
    title: title,
    text: text,
    heightAuto: false,
    icon: icon
  });
}
export function SwalFireNoButtons(title, text, icon, timeout) {
  Swal.fire({
    title: title,
    text: text,
    heightAuto: false,
    showConfirmButton: false,
    icon: icon,
    timer: timeout
  });
}
